mapper.sql:
WHERE BRDDELETEFLAG='N' AND TBG.BGDELETEFLAG='N'

    AND TB.BGNO=#{bgno}
  

    
         ${item} LIKE CONCAT('%', #{searchKeyword},'%' )

mapper.select:
SELECT BRDNO, BRDTITLE, DATE_FORMAT(BRDDATE,'%Y-%m-%d') BRDDATE, BRDNOTICE, BRDLIKE , TB.USERNO, USERNM BRDWRITER, BGNAME, TB.BGNO , (SELECT COUNT(*) FROM TBL_BOARDREAD WHERE BRDNO=TB.BRDNO) BRDHIT , (SELECT COUNT(*) FROM TBL_BOARDFILE WHERE BRDNO=TB.BRDNO) FILECNT , (SELECT COUNT(*) FROM TBL_BOARDREPLY WHERE BRDNO=TB.BRDNO AND REDELETEFLAG='N') REPLYCNT FROM TBL_BOARD TB INNER JOIN COM_USER CU ON TB.USERNO=CU.USERNO INNER JOIN TBL_BOARDGROUP TBG ON TBG.BGNO=TB.BGNO WHERE BRDDELETEFLAG='N' AND BRDNOTICE='Y'
AND TB.BGNO=#{bgno}
ORDER BY BRDNO DESC LIMIT 3

mapper.update:
UPDATE TBL_BOARD
   SET BRDTITLE=#{brdtitle}
     , BRDMEMO=#{brdmemo} 
     , BRDNOTICE=#{brdnotice}
     , LASTDATE=NOW()
     , LASTUSERNO=#{userno}
 WHERE BRDDELETEFLAG='N'
   AND BRDNO=#{brdno}


       INSERT INTO TBL_BOARDREAD(BRDNO, USERNO, READDATE)
                VALUES (#{field1}, #{field2}, NOW())
                    ON DUPLICATE KEY UPDATE READDATE=NOW();

mapper.insert:

       UPDATE TBL_BOARD
          SET BRDLIKE = BRDLIKE + 1 
        WHERE BRDNO=#{field1}

mapper.delete:
UPDATE TBL_BOARD
   SET BRDDELETEFLAG='Y'
     , LASTDATE=NOW()
     , LASTUSERNO=#{userno}
 WHERE BRDNO=#{brdno}


       DELETE 
         FROM TBL_BOARDFILE
        WHERE FILENO IN (
             
                    ${item}
               
       )

mapper.insert:
    
        SELECT IFNULL(MAX(RENO),0)+1 FROM TBL_BOARDREPLY
    

    INSERT INTO TBL_BOARDREPLY(BRDNO, RENO, USERNO, REDELETEFLAG, REMEMO, REDATE, LASTDATE, LASTUSERNO, REORDER, REPARENT, REDEPTH)
    VALUES (#{brdno}, #{reno}, #{userno}, 'N', #{rememo}, NOW(), NOW(), #{userno}, #{reorder},
               
                   #{reno}, 0
                   #{reparent}, #{redepth}
               
           )

mapper.select:
SELECT BRDNO, REDEPTH+1 REDEPTH, REORDER 
  FROM TBL_BOARDREPLY
 WHERE RENO=#{reparent}

mapper.update:

       UPDATE TBL_BOARDREPLY
          SET REORDER = REORDER + 1 
        WHERE BRDNO=#{brdno} AND REORDER>#{reorder}

mapper.select:
SELECT IFNULL(MAX(REORDER),0)+1
  FROM TBL_BOARDREPLY
 WHERE BRDNO=#{brdno}

mapper.update:

       UPDATE TBL_BOARDREPLY
          SET REDELETEFLAG='Y' 
            , LASTDATE=NOW()
            , LASTUSERNO=#{userno}
        WHERE RENO=#{reno}

UPDATE TBL_BOARDREPLY
   SET REMEMO=#{rememo} 
     , LASTDATE=NOW()
     , LASTUSERNO=#{userno}
 WHERE RENO=#{reno}
